// Kiểm tra xem biến 'listImage' đã được khai báo trước đó chưa
if (!window.listImage) {
    const listImage = document.querySelector('.dakhoa__slide-show-image');
    const imgs = document.querySelectorAll('.dakhoa__slide-show-image img:nth-child(-n+4)');
    const length = imgs.length;
    let current = 0;

    const handleChangeSlide = () => {
        if(current == length - 1){
            current = 0;
            listImage.style.transform = `translate(0px)`;
            document.querySelector('.active').classList.remove('active');
            document.querySelector('.index-item-' + current).classList.add('active');
        } else {
            current++;
            let width = imgs[0].offsetWidth;
            listImage.style.transform = `translate(${width * -1 * current}px)`;
            document.querySelector('.active').classList.remove('active');
            document.querySelector('.index-item-' + current).classList.add('active');
        }
    }

   setInterval(handleChangeSlide, 3000);
}